import React from 'react';

const SpotifyPlayerShorter = props => {
    return (
        <iframe
            title={"Spotify"}
            className={"SpotifyPlayer"}
            src={"https://embed.spotify.com/?uri=" + props.uri + "&view=list&theme=black"}
            width={800}
            height={200}
            frameBorder={0}
            allowTransparency={"true"} 
        >
            <p>The URI is {props.uri}</p>
        </iframe>
    );
};

export default SpotifyPlayerShorter;
