import React, { Component } from 'react';
import classes from '../styling/App.module.css';
import styled, { ThemeProvider } from 'styled-components';
import { selected, unselected } from '../styling/themes';

class HeaderBar extends Component {
    constructor(props) {
        super(props);

        /***********************
         * Set menu options here
         ***********************/ 
        this.menuOptions = ['Home', 'Portfolio', 'Contact'];

        // Menu item styling
        this.menuItem = styled.div`
            display: inline-block;
            color: ${props => props.theme.col};
            text-align: center;
            padding-top: 0.4rem;
            padding-bottom: 0.4rem;
            width: ${100 / this.menuOptions.length}%;
            font-weight: bold;
            transition: 0.25s;
            &:hover {
                color: ${props => props.theme.hoverCol};
                cursor: pointer;
            }
        `;

        this.menuItemSelected = styled.div`
             display: inline-block;
             color: white;
             text-align: center;
             padding-top: 0.4rem;
             padding-bottom: 0.4rem;
             width: ${100 / this.menuOptions.length}%;
             font-weight: bold;
             transition: 0.25s;
             &:hover {
                 color: #C5FF0F;
                 cursor: pointer;
             }
        `;

        this.menuItemUnselected = styled.div`
            display: inline-block;
            color: #90D60D;
            text-align: center;
            padding-top: 0.4rem;
            padding-bottom: 0.4rem;
            width: ${100 / this.menuOptions.length}%;
            font-weight: bold;
            transition: 0.25s;
            &:hover {
                color: #A3F20F;
                cursor: pointer;
            }
        `;

        // this.selected = {
        //     col: 'white',
        //     hoverCol: '#C5FF0F'
        // }
        // this.unselected = {
        //     col: '#90D60D',
        //     hoverCol: '#A3F20F'
        // }

        // // Set up div elements for each 
        // this.menuItems = this.menuOptions.map(tab => (
        //     <this.menuItem 
        //         onClick={() => this.props.buttonClicked(tab)}
        //         key={tab}
        //     >
        //         {tab}
        //     </this.menuItem>
        // ));
    }

    menuItemsReturn = () => {
        // return (
        //     this.menuItems.map(item => (
        //         <ThemeProvider
        //             theme={() => {
        //                 if (item.key === this.props.selectedItem) {
        //                     return selected;
        //                 }
        //                 else {
        //                     return unselected;
        //                 }
        //             }}
        //             key={item.key}
        //         >
        //             {item}
        //         </ThemeProvider>
        //     ))
        // );
        return this.menuOptions.map(option => {
            return (
                <p>this is a test {option}</p>
            );
        });
    };

    render() {
        return(
            <div className={`${classes.App} ${classes.MenuBar}`}>
                {this.menuItemsReturn()}
            </div>
        );
    }
}

export default HeaderBar;
